function run(input) {
    const data = JSON.parse(input)
    let carrosel = []

    for (let i = 0; i < data.length; i++) {
        let json = {
            header: {
                type: "application/vnd.lime.media-link+json",
                value: {
                    title: ` ${data[i].brand.toUpperCase()} ${data[i].model.toUpperCase()}  ${data[i].year.split("-")[0]}`,
                    text: `R$ ${data[i].price}`,
                    type: "image/jpeg",
